update.foreach:
		UPDATE 
				    RIS0211 
	    SET 	
		    		APPN_OUTP_PSSB_CNT		=		 #{item.appnOutpPssbCnt}
		     	  , APPN_INPT_PSSB_CNT 		=		 #{item.appnInptPssbCnt}
		     	  , APPN_HLXM_PSSB_CNT 		=		 #{item.appnHlxmPssbCnt}
 		WHERE 		
	 	 			HSPT_ID					= 		 #{item.hsptId}
		   		AND IMGN_ROOM_CD 			= 		 #{item.imgnRoomCd} 
		   		AND EXMN_DATE 				= 		 #{item.exmnDate} 
		   		AND STRT_TIME 				= 		 #{item.strtTime}

mapper.insert:
INSERT 
		INTO RIS0211 
		(
			HSPT_ID
		  , IMGN_ROOM_CD
		  , EXMN_DATE
		  , STRT_TIME
		  , END_TIME
		  , APPN_OUTP_PSSB_CNT
		  , APPN_INPT_PSSB_CNT
		  , APPN_HLXM_PSSB_CNT 
		 ) 		 

 VALUES (
 			#{item.hsptId}
 		  , #{item.imgnRoomCd}
 		  , #{item.exmnDate}
 		  , #{item.strtTime}
 		  , #{item.endTime}
 		  , #{item.appnOutpPssbCnt}
 		  , #{item.appnInptPssbCnt}
 		  , #{item.appnHlxmPssbCnt}
 		 )

mapper.delete:
DELETE 
  FROM 
  			 RIS0211 
 WHERE 		

	 	 	 1				=	1  
	    AND  HSPT_ID 		= 	#{item.hsptId} 
 		AND  IMGN_ROOM_CD 	= 	#{item.imgnRoomCd}
 		AND  EXMN_DATE 		=   #{item.exmnDate} 
 		AND  STRT_TIME		=   #{item.strtTime}
