mapper.delete:
DELETE
 FROM  RIS0101
WHERE  HSPT_ID = #{hspt_id}
	AND	 LRGC_CD = #{lrgc_cd}

DELETE
FROM  RIS0102
WHERE  HSPT_ID = #{hspt_id}
	AND	 LRGC_CD = #{lrgc_cd}
	AND	 MDDL_CD = #{mddl_cd}

DELETE
FROM  RIS0103
WHERE  HSPT_ID = #{hspt_id}
	AND	 LRGC_CD = #{lrgc_cd}
	AND	 MDDL_CD = #{mddl_cd}
	AND	 SMLL_CD = #{smll_cd}

mapper.select:
SELECT COUNT(1) AS cnt
	FROM ris0101 a
  WHERE a.lrgc_cd = #{code}
