mapper.select:
SELECT MENU_ID, PGRM_ID, MENU_NAME, MENU_NAME_SUB, UPPER_MENU_ID, MENU_LEVEL, OTPT_SQNC, USE_YN
FROM risgrmu
WHERE MENU_GROUP_ID = #{type}

SELECT MENU_GROUP_ID, MENU_NAME, MENU_ID, MENU_GRADE, USE_YN, OTPT_SQNC
FROM risurmn
WHERE LOGIN_ID = #{type}
AND HSPT_ID = #{type2}

mapper.insert:
INSERT INTO risgrup (
	MENU_GROUP_ID, MENU_GROUP_NAME, OTPT_SQNC, USE_YN
)
VALUES (
	#{menuGroupId}, #{menuGroupName}, #{otptSqnc}, #{useYn}
)
